  def Adj_weightedMinHash(ngram: Array[Int], rs:Array[Array[Double]] , cs:Array[Array[Double]], b:Array[Array[Double]]): Array[Int] = {


    //SshParams.updateCoefficents(ngram.length)


    val result = ArrayBuffer.empty[Int ]

    var kk = 0
    for (s <- 0 until 9) {
      val tks = Array.fill[Double](ngram.length)(0)
      val yks = Array.fill[Double](ngram.length)(0)
      val aks = Array.fill[Double](ngram.length)(0)




      for (k <- 0 until ngram.length-1 if ngram(k) != 0) {
        //print ("RS:")
        //rs_t(s).foreach(println)
        val tk: Double = (math.log(ngram(k)) / rs(s)(k)) + b(s)(k)
        val yk: Double  = math.exp(rs(s)(k) * (tk - b(s)(k)))
        val ak: Double = cs(s)(k) / (yk * math.exp(rs(s)(k)))

        tks(k) = tk
        yks(k) = yk
        aks(k) = ak
        kk = k

      }



      val k_s = argMinIndex(aks)
      //result += ((k_s, yks(k_s).toInt))
      //result += ((k_s, tks(k_s).toInt))
      result += k_s
    }
    //println("k is " + kk)

    result.toArray
  }

  private def argMinIndex(value: Array[Double]): Int = {
    var min = Double.MaxValue
    var returnindex= 0
    for ( i <- 0 until value.length-1) {
      if (value(i) < min )  {
        min = value(i)
        returnindex = i
      }

    }

    return returnindex
  }




